import React, {Fragment} from 'react';
import { Link } from 'react-router-dom';
import store from '../../store';

// import store from '../../store';

class Menu extends React.Component {

  constructor(props)
  {
    super(props);
    this.state = {
      login: store.getState().login,
      menu: ''
    }
  }


  render() {

    if(this.state.login){
      this.setState({
        menu : '<li><Link to="/logout">Logout</Link></li><li><Link to="/dashboard">Dashboard</Link></li>'
      });
    } else {
      this.setState({
        menu : '<li><Link to="/login">Login</Link></li><li><Link to="/register">Register</Link></li>'
      });
    }

    return (
      <div className="Menu">
            <ul>
              <li >
                <Link to="/">Home</Link>
              </li>
              <Fragment>
              { this.state.menu }
              </Fragment>

            </ul>
      </div>
    )
  }
}

export default Menu;
