import React from 'react';
import { Link } from 'react-router-dom';
import store from '../../store';

// import store from '../../store';

class Menu extends React.Component {

  constructor(props)
  {
    super(props)
    this.state = {
      login: store.getState().login
    }

    if(this.state.login){
      let menu = '<li><Link to="/logout">Logout</Link></li><li><Link to="/dashboard">Dashboard</Link></li>';
    } else {
      let menu = '<li><Link to="/login">Login</Link></li><li><Link to="/register">Register</Link></li>';
    }
  }


  render() {
    return (
      <div className="Menu">
            <ul>
              <li >
                <Link to="/">Home</Link>
              </li>
              { this.props.menu }
            </ul>
      </div>
    )
  }
}

export default Menu;
